namespace BocciaCoaching.Models.DTO.General
{
    public class ResponseNewRecordDto
    {
        public ResponseNewRecordDto()
        {
            Success = true;
            Message = string.Empty;
        }
        public bool Success { get; set; }
        public string Message { get; set; }
    }
}

﻿namespace BocciaCoaching.Models.DTO.General
{
    public class ResponseNewRecordDto
    {
        public ResponseNewRecordDto()
        {
            Success = true;
            Message = string.Empty;
        }
        public bool Success { get; set; }
        public string Message { get; set; }
        public int? NewRecordId { get; set; }
    }
}

using BocciaCoaching.Data;
using BocciaCoaching.Models.DTO.General;
using BocciaCoaching.Models.DTO.Team;
using BocciaCoaching.Models.Entities;
using BocciaCoaching.Repositories.Interfaces.ITeams;
using Microsoft.EntityFrameworkCore;

namespace BocciaCoaching.Repositories.Teams
{
    public class TeamRepository(ApplicationDbContext context) : ITeamRepository
    {
        /// <summary>
        /// Método para agregar un nuevo equipo
        /// </summary>
        /// <param name="requestTeamDto">Información del equipo</param>
        /// <returns></returns>
        public async Task<ResponseNewRecordDto> AddTeam(RequestTeamDto requestTeamDto)
        {
            try
            {
                var team = new Team()
                {
                    CoachId = requestTeamDto.CoachId,
                    NameTeam = requestTeamDto.NameTeam,
                    Description = requestTeamDto.Description,
                };

                await context.Teams.AddAsync(team);
                await context.SaveChangesAsync();
                return new ResponseNewRecordDto()
                {
                    Success = true
                };
            }
            catch (Exception ex)
            {
                return new ResponseNewRecordDto()
                {
                    Success = true,
                    Message = ex.Message

                };
            }

        }

        /// <summary>
        /// Método para agregar mienbros al equipo
        /// </summary>
        /// <param name="requestTeamMemberDto"></param>
        /// <returns></returns>
        public async Task<ResponseNewRecordDto> AddTeamMember(RequestTeamMemberDto requestTeamMemberDto)
        {
            try
            {
                var teamUser = new TeamUser()
                {
                    TeamId = requestTeamMemberDto.TeamId,
                    UserId = requestTeamMemberDto.UserId,
                    DateCreation = new DateTime(),
                };

                await context.TeamsUsers.AddAsync(teamUser);
                await context.SaveChangesAsync();
                return new ResponseNewRecordDto()
                {
                    Success = true
                };
            }
            catch (Exception ex)
            {
                return new ResponseNewRecordDto()
                {
                    Success = true,
                    Message = ex.Message

                };
            }

        }
        /// <summary>
        /// Método para obtener los equipos al que pertenece los entrenadores 
        /// </summary>
        /// <returns></returns>
        public async Task<List<Team>> GetTeamsForUser(RequestTeamDto requestTeamDto)
        {
            try
            {
                var teams = await context.TeamsUsers
                    .Where(tu => tu.UserId == requestTeamDto.CoachId)
                    .Include(tu => tu.Team) // Incluye la info del equipo
                    .Select(tu => tu.Team)  // Proyecta solo el Team
                    .ToListAsync();

                return teams;
            }
            catch (Exception ex)
            {
                Console.WriteLine($"Error en GetTeamsForUserAsync: {ex.Message}");
                return new List<Team>(); // Retorna lista vacía si hay error
            }
        }
        /// <summary>
        /// Método para obtener los integrantes de un equipo
        /// </summary>
        /// <param name="requestGetUserForTeamDto"></param>
        /// <returns></returns>
        public async Task<ResponseContract<List<User>>> GetUsersForTeam(RequestGetUserForTeamDto requestGetUserForTeamDto)
        {
            try
            {
                var users = await context.TeamsUsers
                .Where(tu => tu.TeamId == requestGetUserForTeamDto.TeamId &&
                             tu.User.UserRoles.Any(ru => ru.RolId == requestGetUserForTeamDto.RolId))
                .Include(tu => tu.User)
                .ThenInclude(u => u.UserRoles)
                .ThenInclude(ru => ru.Rol)
                .Select(tu => tu.User)
                .ToListAsync();

                return ResponseContract<List<User>>.Ok(users, "Ususarios obtenidos satisfactoriamente");
            }
            catch (Exception ex)
            {
                Console.WriteLine($"Error en GetUsersForTeam: {ex.Message}");
                return ResponseContract<List<User>>.Fail(ex.Message)!;
            }
        }

        /// <summary>
        /// Método para actualizar la imagen del equipo
        /// </summary>
        /// <param name="requestUpdateImageTeamDto"></param>
        /// <returns></returns>
        /// <exception cref="ArgumentException"></exception>
        public async Task<bool> UpdateTeam(RequestUpdateTeamDto requestUpdateImageTeamDto)
        {
            try
            {
                var team = await context.Teams.FirstOrDefaultAsync(t => t.TeamId == requestUpdateImageTeamDto.TeamId);

                if (team == null)
                    return false;

                if (!string.IsNullOrEmpty(requestUpdateImageTeamDto.Image))
                {
                    // Validar que la cadena base64 sea válida
                    if (string.IsNullOrWhiteSpace(requestUpdateImageTeamDto.Image))
                        throw new ArgumentException("La imagen no puede estar vacía.");

                    // Si quieres validar que realmente sea base64
                    if (!IsBase64String(requestUpdateImageTeamDto.Image))
                        throw new ArgumentException("El formato de imagen no es válido.");

                    team.Image = requestUpdateImageTeamDto.Image;
                }

                team.Bc1 = requestUpdateImageTeamDto.Bc1;
                team.Bc2 = requestUpdateImageTeamDto.Bc2;
                team.Bc3 = requestUpdateImageTeamDto.Bc3;
                team.Bc4 = requestUpdateImageTeamDto.Bc4;

                team.Country = requestUpdateImageTeamDto.Country;
                team.Region = requestUpdateImageTeamDto.Region;

                team.UpdatedAt = DateTime.UtcNow;

                context.Teams.Update(team);
                await context.SaveChangesAsync();

                return true;
            }
            catch (Exception ex)
            {
                // Aquí puedes loguear el error si tienes un sistema de auditoría
                Console.WriteLine($"Error al actualizar la imagen: {ex.Message}");
                return false;
            }
        }
        private bool IsBase64String(string base64)
        {
            Span<byte> buffer = new Span<byte>(new byte[base64.Length]);
            return Convert.TryFromBase64String(base64, buffer, out _);
        }



    }
}

﻿using BocciaCoaching.Data;
using BocciaCoaching.Models.DTO.General;
using BocciaCoaching.Models.DTO.Team;
using BocciaCoaching.Models.Entities;
using BocciaCoaching.Repositories.Interfaces.ITeams;
using Microsoft.EntityFrameworkCore;

namespace BocciaCoaching.Repositories.Teams
{
    public class TeamRepository(ApplicationDbContext context) : ITeamRepository
    {
        /// <summary>
        /// Método para agregar un nuevo equipo
        /// </summary>
        /// <param name="requestTeamDto">Información del equipo</param>
        /// <returns></returns>
        public async Task<ResponseNewRecordDto> AddTeam(RequestTeamDto requestTeamDto)
        {
            try
            {
                var team = new Team()
                {
                    CoachId = requestTeamDto.CoachId,
                    NameTeam = requestTeamDto.NameTeam,
                    Description = requestTeamDto.Description,
                };

                await context.Teams.AddAsync(team);
                await context.SaveChangesAsync();

                // Relacionar automáticamente al entrenador con el equipo
                var teamUser = new TeamUser()
                {
                    TeamId = team.TeamId,
                    UserId = requestTeamDto.CoachId,
                    DateCreation = DateTime.Now
                };

                await context.TeamsUsers.AddAsync(teamUser);
                await context.SaveChangesAsync();

                return new ResponseNewRecordDto()
                {
                    Success = true,
                    NewRecordId = team.TeamId
                };
            }
            catch (Exception ex)
            {
                return new ResponseNewRecordDto()
                {
                    Success = false,
                    Message = ex.Message

                };
            }

        }

        /// <summary>
        /// Método para agregar mienbros al equipo
        /// </summary>
        /// <param name="requestTeamMemberDto"></param>
        /// <returns></returns>
        public async Task<ResponseNewRecordDto> AddTeamMember(RequestTeamMemberDto requestTeamMemberDto)
        {
            try
            {
                var teamUser = new TeamUser()
                {
                    TeamId = requestTeamMemberDto.TeamId,
                    UserId = requestTeamMemberDto.UserId,
                    DateCreation = DateTime.Now,
                };

                await context.TeamsUsers.AddAsync(teamUser);
                await context.SaveChangesAsync();
                return new ResponseNewRecordDto()
                {
                    Success = true
                };
            }
            catch (Exception ex)
            {
                return new ResponseNewRecordDto()
                {
                    Success = false,
                    Message = ex.Message

                };
            }

        }
        /// <summary>
        /// Método para obtener los equipos al que pertenece los entrenadores 
        /// </summary>
        /// <returns></returns>
        public async Task<List<Team>> GetTeamsForUser(RequestTeamDto requestTeamDto)
        {
            try
            {
                var teamUsers = await context.TeamsUsers
                    .Where(tu => tu.UserId == requestTeamDto.CoachId && tu.Team != null)
                    .Include(tu => tu.Team)
                    .ToListAsync();

                return teamUsers.Where(tu => tu.Team != null).Select(tu => tu.Team!).ToList()!;
            }
            catch (Exception ex)
            {
                Console.WriteLine($"Error en GetTeamsForUserAsync: {ex.Message}");
                return new List<Team>(); // Retorna lista vacía si hay error
            }
        }
        /// <summary>
        /// Método para obtener los integrantes de un equipo
        /// </summary>
        /// <param name="requestGetUserForTeamDto"></param>
        /// <returns></returns>
        public async Task<ResponseContract<List<User>>> GetUsersForTeam(RequestGetUserForTeamDto requestGetUserForTeamDto)
        {
            try
            {
                var teamUsers = await context.TeamsUsers
                .Where(tu => tu.TeamId == requestGetUserForTeamDto.TeamId && tu.User != null)
                .Include(tu => tu.User!)
                    .ThenInclude(u => u.UserRoles)
                    .ThenInclude(ru => ru.Rol)
                .ToListAsync();

                var users = teamUsers
                    .Where(tu => tu.User != null && 
                           tu.User.UserRoles != null && 
                           tu.User.UserRoles.Any(ru => ru.RolId == requestGetUserForTeamDto.RolId))
                    .Select(tu => tu.User!)
                    .ToList()!;

                return ResponseContract<List<User>>.Ok(users, "Ususarios obtenidos satisfactoriamente");
            }
            catch (Exception ex)
            {
                Console.WriteLine($"Error en GetUsersForTeam: {ex.Message}");
                return ResponseContract<List<User>>.Fail(ex.Message);
            }
        }

        /// <summary>
        /// Método para actualizar la imagen del equipo
        /// </summary>
        /// <param name="requestUpdateImageTeamDto"></param>
        /// <returns></returns>
        /// <exception cref="ArgumentException"></exception>
        public async Task<bool> UpdateTeam(RequestUpdateTeamDto requestUpdateImageTeamDto)
        {
            try
            {
                var team = await context.Teams.FirstOrDefaultAsync(t => t.TeamId == requestUpdateImageTeamDto.TeamId);

                if (team == null)
                    return false;

                if (!string.IsNullOrEmpty(requestUpdateImageTeamDto.Image))
                {
                    // Validar que la cadena base64 sea válida
                    if (string.IsNullOrWhiteSpace(requestUpdateImageTeamDto.Image))
                        throw new ArgumentException("La imagen no puede estar vacía.");

                    // Si quieres validar que realmente sea base64
                    if (!IsBase64String(requestUpdateImageTeamDto.Image))
                        throw new ArgumentException("El formato de imagen no es válido.");

                    team.Image = requestUpdateImageTeamDto.Image;
                }

                team.Bc1 = requestUpdateImageTeamDto.Bc1;
                team.Bc2 = requestUpdateImageTeamDto.Bc2;
                team.Bc3 = requestUpdateImageTeamDto.Bc3;
                team.Bc4 = requestUpdateImageTeamDto.Bc4;

                team.Country = requestUpdateImageTeamDto.Country;
                team.Region = requestUpdateImageTeamDto.Region;

                team.UpdatedAt = DateTime.UtcNow;

                context.Teams.Update(team);
                await context.SaveChangesAsync();

                return true;
            }
            catch (Exception ex)
            {
                // Aquí puedes loguear el error si tienes un sistema de auditoría
                Console.WriteLine($"Error al actualizar la imagen: {ex.Message}");
                return false;
            }
        }
        private bool IsBase64String(string base64)
        {
            Span<byte> buffer = new Span<byte>(new byte[base64.Length]);
            return Convert.TryFromBase64String(base64, buffer, out _);
        }



    }
}

using BocciaCoaching.Models.DTO.General;
using BocciaCoaching.Models.DTO.Statistic;
using BocciaCoaching.Models.DTO.Team;
using BocciaCoaching.Models.Entities;
using BocciaCoaching.Repositories.Interfaces;
using BocciaCoaching.Repositories.Interfaces.ITeams;
using BocciaCoaching.Repositories.Statistic.Interfce;
using BocciaCoaching.Services.Interfaces;

namespace BocciaCoaching.Services
{
    public class TeamService : ITeamService
    {
        private readonly ITeamRepository _teamRepository;
        private readonly IStatisticAssessStrength _statisticAssessStrength;
        public TeamService(ITeamRepository teamRepository, IStatisticAssessStrength statisticAssessStrength)
        {
            _teamRepository = teamRepository;
            _statisticAssessStrength = statisticAssessStrength;
        }

        /// <summary>
        /// Método para agregar un nuevo equipo
        /// </summary>
        /// <param name="requestTeamDto">Información del equipo</param>
        /// <returns></returns>
        public async Task<ResponseNewRecordDto> AddTeam(RequestTeamDto requestTeamDto)
        {
            return await _teamRepository.AddTeam(requestTeamDto);
        }

        /// <summary>
        /// Método para agregar mienbros al equipo
        /// </summary>
        /// <param name="requestTeamDto">Información del mienbro equipo</param>
        /// <returns></returns>
        public async Task<ResponseNewRecordDto> AddTeamMember(RequestTeamMemberDto requestTeamMemberDto)
        {
            return await _teamRepository.AddTeamMember(requestTeamMemberDto);
        }


        public async Task<List<Team>> GetTeamsForUser(RequestTeamDto requestTeamDto)
        {
            return  await _teamRepository.GetTeamsForUser(requestTeamDto);
        }

        public async Task<ResponseContract<List<User>>> GetUsersForTeam(RequestGetUserForTeamDto requestGetUserForTeamDto)
        {
            return await _teamRepository.GetUsersForTeam(requestGetUserForTeamDto);
        }

        public async Task<bool> UpdateTeam(RequestUpdateTeamDto requestUpdateImageTeamDto)
        {
            return await _teamRepository.UpdateTeam(requestUpdateImageTeamDto);
        }

        public async Task<ResponseContract<List<StrengthTestSummaryDto>>> GetRecentStatistics(RequestInfoCoachAndTeam requestInfoCoachAndTeam)
        {
            try
            {
                 var responseRecentSta = await _statisticAssessStrength.GetRecentStatistics(requestInfoCoachAndTeam.CoachId, requestInfoCoachAndTeam.TeamId);
                 return responseRecentSta;
            }
            catch (Exception e)
            {
                Console.WriteLine(e);
                throw;
            }
        }

    }
}

